import itertools


class Solution:
    def longestPalindrome(self, s: str) -> str:
        # Generate all possible substrings, sorted by length.
        substrings = sorted([
            s[x:y] for (x, y) in
            itertools.combinations(range(len(s)+1), r=2)
        ], key=len)

        # Define variables that will be used when searching for the longest
        # palindrome.
        longest_palindrome = ""
        all_palindromes = {}

        # Create a helper variable to update the palindrome variables.
        def update_vars(substring):
            nonlocal all_palindromes, longest_palindrome
            all_palindromes[substring] = True
            if len(substring) > len(longest_palindrome):
                longest_palindrome = substring

        # Search for the longest palindrome using a dictionary cache.
        for substring in substrings:
            if (len(substring) == 1 or
                    len(substring) == 2 and substring[0] == substring[1]):
                update_vars(substring)
                continue
            inside = substring[1:-1]
            if substring[0] == substring[-1] and inside in all_palindromes:
                update_vars(substring)

        return longest_palindrome


if __name__ == '__main__':
    s = Solution()

    for input, exp, name in [
        ("babad", "bab", "case 1"),
        ("cbbd",  "bb",  "case 2"),
        ("a",     "a",   "case 3"),
        ("ac",    "a",   "case 4"),
        ("", "", "performance 1"),
        ("", "", "performance 2"),
    ]:
        res = s.longestPalindrome(input)
        if res != exp:
            print(f"{name}, exp: {exp}, res: {res}")
        else:
            print(f"passed: {name}")
